'    ',
'    c_name constant varchar2(30) := apex_plugin.get_input_name_for_item;',
'',
'begin',
'',
'    -- Get API key for JS file name',
'    l_js_params := ''?key='' || l_api_key || ''&libraries=places'';',
'',
